import React, { useEffect, useState } from 'react';
import ContainerDefault from '~/components/layouts/ContainerDefault';
import HeaderDashboard from '~/components/shared/headers/HeaderDashboard';
import { connect, useDispatch } from 'react-redux';
import { Radio, Input, Space } from 'antd';
import { toggleDrawerMenu } from '~/store/app/action';
import { useRouter } from 'next/router';
import { getCustomers } from '~/networks/api';

const CreateProductPage = () => {

    const router = useRouter()

    const [customers, setCustomers] = useState([])


    const dispatch = useDispatch();
    const [rnumber, setRnumber] = useState(0)
    const [receiptDate, setReceiptDate] = useState(new Date)
    const [companyName, setCompanyName] = useState("")
    const [companyData, setCompanyData] = useState({})
    const [hsn, setHsnCode] = useState("")
    const [unitOfMeasure, setUnitOfMeasure] = useState("")
    const [paymentOption, setPaymentOption] = useState("")
    const [ptype, setPtype] = useState("")
    const [cgst, setCgst] = useState(0)
    const [sgst, setSgst] = useState(0)
    const [igst, setIgst] = useState(0)
    const [cessPer, setCessPer] = useState(0)
    const [cessAmt, setCessAmt] = useState(0)
    const [sellPrice, setSellPrice] = useState(0)
    const [sellPriceWithTax, setSellPriceWithTax] = useState(0)
    const [purchasePrice, setPurchasePrice] = useState(0)
    const [purchasePriceWithTax, setPurchasePriceWithTax] = useState(0)

    useEffect(() => {
        dispatch(toggleDrawerMenu(false));
        loadCustomerData()
    }, []);


    useEffect(() => {
        console.log(companyName);
        const companyDetails = customers.filter((data) => (data.id == companyName))
        console.log(companyDetails);
    }, [companyName])


    const loadCustomerData = async () => {
        const res = await getCustomers();
        console.log(res.data);
        setCustomers(res.data)
    }


    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = {
            name: pname,
            note: pnote,
            hsn,
            unit_measurement: unitOfMeasure,
            stock,
            product_type: ptype,
            cgst,
            sgst,
            igst,
            cess: cessPer,
            cess_amt: cessAmt,
            sell_price: sellPrice,
            sell_price_tax: sellPriceWithTax,
            purchase_price: purchasePrice,
            purchase_price_tax: purchasePriceWithTax,
        }

        const response = await fetch('http://localhost:1337/products', {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            credentials: 'same-origin', // include, *same-origin, omit
            headers: {
                'Content-Type': 'application/json'
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: 'follow', // manual, *follow, error
            referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
            body: JSON.stringify(data) // body data type must match "Content-Type" header
        });


    }



    return (
        <ContainerDefault title="Create new Outward Receipt">
            <HeaderDashboard
                title="Outward Receipt"
                description="InvoiceDF Create New Outward Receipt "
            />
            <section className="ps-new-item">
                <form
                    onSubmit={e => handleSubmit(e)}
                    className="ps-form ps-form--new-product">
                    <div className="ps-form__content">
                        <div className="row">
                            <div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12">
                                <figure className="ps-block--form-box">
                                    <figcaption>General</figcaption>
                                    <div className="ps-block__content">
                                        <div className="form-group">
                                            <label>
                                                Receipt Number<sup>*</sup>
                                            </label>
                                            <input
                                                value={rnumber}
                                                className="form-control"
                                                onChange={e => setRnumber(parseInt(e.target.value))}
                                                type="number"
                                                placeholder="Enter product name..."
                                            />
                                        </div>

                                        <div className="form-group">
                                            <label>
                                                Company Name<sup>*</sup>
                                            </label>
                                            <select className="form-control" onChange={e => setCompanyName(e.target.value)}
                                            >
                                                <option value="">Select Customers</option>
                                                {

                                                    customers.map((data, key) => (

                                                        <option key={data.id} value={data.id}>{data.customer_name}</option>
                                                    ))

                                                }
                                            </select>
                                        </div>
                                        <div className="form-group">
                                            <label>
                                                GST/PAN Number<sup>*</sup>
                                            </label>
                                            <input
                                                className="form-control"
                                                onChange={e => setData({ name: e.target.value })}
                                                type="text"
                                                placeholder="Enter product name..."
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label>
                                                Address
                                            </label>
                                            <textarea
                                                onChange={e => setData({ note: e.target.value })}
                                                className="form-control"
                                                rows="5"
                                                name="editordata"></textarea>
                                        </div>

                                    </div>
                                </figure>
                            </div>
                            <div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12">
                                <figure className="ps-block--form-box">
                                    <figcaption>Payment</figcaption>
                                    <div className="ps-block__content">
                                        <div className="form-group">
                                            <label>Amount</label>
                                            <input
                                                value={cgst}
                                                onChange={e => setCgst(parseInt(e.target.value))}
                                                placeholder="values in (₹)"
                                                className="form-control"
                                                type="number"
                                            />
                                        </div>

                                        <div className="form-group">
                                            <label>
                                                Payment Date
                                                <sup>*</sup>
                                            </label>
                                            <input
                                                value={receiptDate}
                                                onChange={e => setReceiptDate(e.target.value)}
                                                className="form-control"
                                                type="date"
                                                placeholder=""
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label style={{ marginRight: 10 }}>
                                                Payment Option
                                                <sup>*</sup>
                                            </label>

                                            <Radio.Group onChange={e => setPaymentOption(e.target.value)} value={paymentOption}>
                                                <Space direction="vertical">
                                                    <Radio value="on_account">On Account</Radio>
                                                    <Radio value="on_invoice">On Invoice</Radio>

                                                </Space>
                                            </Radio.Group>
                                        </div>
                                        <div className="form-group">
                                            <label>
                                                Payment Type<sup>*</sup>
                                            </label>
                                            <select className="form-control"
                                                onChange={e => setPtype(e.target.value)}
                                            >
                                                <option value="">Select Type</option>
                                                <option value="taxable">Cash</option>
                                                <option value="taxable">Cheque</option>
                                                <option value="nill_rated">Online/Transfer</option>
                                                <option value="exempted">Bank/Transfer</option>
                                                <option value="non_gst">TDS</option>
                                                <option value="non_gst">Bad Debts/Kasar</option>
                                                <option value="non_gst">Currency Exchange Loss</option>
                                            </select>
                                        </div>
                                        <div className="form-group">
                                            <label>
                                                Payment Notes
                                            </label>
                                            <textarea
                                                onChange={e => setData({ note: e.target.value })}
                                                className="form-control"
                                                rows="2"
                                                name="editordata"></textarea>
                                        </div>
                                    </div>
                                </figure>

                            </div>
                        </div>
                    </div>
                    <div className="ps-form__bottom">
                        <a
                            className="ps-btn ps-btn--black"
                            href="#" onClick={() => router.back()}>
                            Back
                        </a>
                        <button type="submit" className="ps-btn">Submit</button>
                    </div>
                </form>
            </section>
        </ContainerDefault>
    );
};
export default connect((state) => state.app)(CreateProductPage);
