import React, { useState, useMemo } from 'react';
// import Container from '@material-ui/core/Container';
// import TextField from '@material-ui/core/TextField';
// import Button from '@material-ui/core/Button';
// import IconButton from '@material-ui/core/IconButton';
// import RemoveIcon from '@material-ui/icons/Remove';
// import AddIcon from '@material-ui/icons/Add';
// import Icon from '@material-ui/core/Icon';
import { v4 as uuidv4 } from 'uuid';
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'

import { useTable } from 'react-table'
// import { makeStyles } from '@material-ui/core/styles';



export const BasicTable = () => {

  const columns = useMemo(() => COLUMNS, [])
  const data = useMemo(() => MOCK_DATA, [])

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    footerGroups,
    rows,
    prepareRow
  } = useTable({
    columns,
    data
  })
  // const classes = useStyles()
  const [inputFields, setInputFields] = useState([
    { id: uuidv4(), firstName: '', lastName: '' },
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("InputFields", inputFields);
  };

  const handleChangeInput = (id, event) => {
    const newInputFields = inputFields.map(i => {
      if (id === i.id) {
        i[event.target.name] = event.target.value
      }
      return i;
    })

    setInputFields(newInputFields);
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, { id: uuidv4(), firstName: '', lastName: '' }])
  }

  const handleRemoveFields = id => {
    const values = [...inputFields];
    values.splice(values.findIndex(value => value.id === id), 1);
    setInputFields(values);
  }

  return (
    <>
      <table className='table' onSubmit={handleSubmit}>

        <thead className='hover'>
          {headerGroups.map(headerGroup => (
            <tr className='tr'{...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th className='th'{...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
            </tr>
          ))}
        </thead>

            <tr className='tr'>

                {
                  inputFields.map(inputField => (
                    <div key={inputField.id}>
                      <th><input
                        name="firstName"
                        label="First Name"
                        variant="filled"
                        value={inputField.firstName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      /></th>
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <input
                        name="lastName"
                        label="Last Name"
                        variant="filled"
                        value={inputField.lastName}
                        onChange={event => handleChangeInput(inputField.id, event)}
                      />
                      <button disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>
                        {/* <RemoveIcon /> */}
                      </button>
                      <button
                        onClick={handleAddFields}
                      >
                        {/* <AddIcon /> */}
                      </button>
                    </div>
                  ))
                }

              </tr>

                <button
                  // className={classes.button}
                  variant="contained"
                  color="primary"
                  type="submit"
                  // endIcon={<Icon>send</Icon>}
                  onClick={handleSubmit}
                >Send</button>
      </table>
    </>
      );
}
