import axios from 'axios';
import decode from 'jwt-decode'

const instance = axios.create({
    baseURL: "http://178.18.250.242:1337",
});

instance.interceptors.request.use(async (config) => {
    const accessToken = 'Bearer ' + localStorage.getItem('accessToken');
    if (localStorage.getItem('accessToken')) {

        config.headers.Authorization = (accessToken ? accessToken : '');
    }
    return config;
});

export const login = async (email, password) => (
    await axios.post('http://178.18.250.242:1337/auth/local', { identifier: email, password })
);
export const getProducts = async () => {
    return await instance.get('products')
}
export const postCustomers = async () => {
    return await instance.get('customers')
}
